import jieba
from sklearn.feature_extraction.text import TfidfVectorizer
from sklearn.naive_bayes import MultinomialNB
from sklearn.pipeline import Pipeline

# 情绪分类标签
emotions =['高兴','恐惧', '难过', '其他']

# 训练数据
train_data =[
  ('今天股市大涨,我的投资终于回本了!', '高兴'),
  ('今天股市大跌,损失惨重啊!', '难过'),
  ('最近股市动荡,损失惨重,真是让人害怕。', '恐惧'),
  ('又亏损了,什么时候才能翻身啊,伤心。', '难过'),
  ('股市平平淡淡,没什么好说的。', '其他'),
  ('我对股市未来充满信心,相信会越来越好!', '高兴'),
]

# 对中文文本进行分词
def cut_words(text):
    return ' '.join(jieba.lcut(text))

# 建立机器学习管道
pipeline = Pipeline([
    ('tfidf', TfidfVectorizer(tokenizer=cut_words)),
    ('clf', MultinomialNB())
])

# 训练模型
pipeline.fit([text for text, emotion in train_data], [emotion for text, emotion in train_data])

